<?php

namespace App\Tests\Unit;

use App\Entity\Agency;

use Symfony\Bundle\FrameworkBundle\Test\KernelTestCase;

class AgencyTest extends KernelTestCase
{
    public function getEntity(): Agency
    {

        return (new Agency())
            ->setName('test')
            ->setAddress("12 avenue general de gaulle")
            ->setTown("Marseille")
            ->setZipCode("13000")
            ->setDescription('test')
            ->setImage('test.jpg')
            ->setPhone('0123456789');
    }

    public function assertHasErrors(Agency $agency, int $number = 0)
    {
        self::bootKernel();
        $container = static::getContainer();
        $errors = $container->get('validator')->validate($agency);
        $messages = [];
        foreach ($errors as $error) {
            $messages[] = $error->getPropertyPath() . '=>' . $error->getMessage();
        }
        $this->assertCount($number, $errors, implode(', ', $messages));
    }

    public function testValidEntity(): void
    {
        $this->assertHasErrors($this->getEntity(), 0);
    }

    public function testInvalidName(): void
    {

        $this->assertHasErrors($this->getEntity()->setName(''), 1);
        $this->assertHasErrors($this->getEntity()->setName('**'), 1);
